import React, { Component } from 'react'
import { connect } from 'react-redux'
import { deleteSV, editSV } from '../../Store/actions/actions'


class TableSV extends Component {
    state = {

    }

    handleInput = (event) => {
        console.log(event)

    }
    render() {
        console.log(this.props)
        const { mangSV } = this.props
        return (
            <div>
                {/* filtering */}
                <div>
                    <p style={{ color: 'green', fontWeight: 'bold' }}  >Tìm kiếm theo mã sinh viên </p>
                    <div className='row'>
                    <input className='form-control my-4' type="text" placeholder='Nhập vào mã sinh viên muốn tìm' onChange={this.handleInput} />  <button className='btn btn-primary mb-4'> Tìm </button>

                    </div>



                </div>
                <table className='table'>
                    <thead className='thead-dark'>
                        <tr className='w-100'>
                            <th scope='col' className='text-left'>Mã SV</th>
                            <th scope='col' className='text-left'>Họ Tên</th>
                            <th scope='col' className='text-left'>Số điện thoại</th>
                            <th scope='col' className='text-left'> Email</th>
                            <th></th>
                            <th></th>

                        </tr>

                    </thead>
                    <tbody>
                        {
                            mangSV.map((SV) => {
                                return (
                                    <tr className='text-left' key={SV.maSV}>
                                        <td>{SV.maSV}</td>
                                        <td>{SV.hoTen}</td>
                                        <td>{SV.soDT}</td>
                                        <td>{SV.email}</td>
                                        <td> <button className='btn btn-danger mr-4' onClick={() => {
                                            this.props.dispatch(deleteSV(SV.maSV))
                                        }}>DELETE</button>
                                            <button className='btn btn-warning' onClick={() => {
                                                this.props.dispatch(editSV(SV.maSV))
                                            }}>EDIT</button> </td>
                                    </tr>
                                )
                            })
                        }

                    </tbody>


                </table>
            </div>
        )
    }
}


const mapStateToProps = (state) => {
    return { ...state.BTform }

}

export default connect(mapStateToProps)(TableSV)
