import React, { Component } from 'react'

export default class FormSV extends Component {

// tạo state nội bộ để lấy value từ input về

state =  {
    sinhVien: {
        maSV: '',
        hoTen: '',
        soDT: '',
        email: '',
    },
    errorMess:{

    }

}

handleState = (event) =>{
const {name,value} = event.target

this.setState({
    sinhVien: {
        ...this.state.sinhVien,
        [name]: value
    }
} )  
}

//validation 
handleValidation = (event) => {
console.log(event)
const {title,name,tooShort,minLength,maxLength} = event.target
const {valueMissing,patternMismatch} = event.target.validity

let errMess =''

if (valueMissing) {
errMess = `${title} không được bỏ trống`
}
if (tooShort) {
errMess = `${title} phải từ ${minLength} tới ${maxLength}`
if (patternMismatch) {
    errMess = `${title} không đúng định dạng`
}
}
}




    render() {
        console.log(this.state)
        return (
            <form action="">
                <h1 className='text-center bg-dark text-light'>Thông Tin Sinh Viên</h1>
                {/* các thẻ input */}
                <div className='row'>
                <div className='col-6 text-left '>
                    <p className='mt-2'>Mã Sinh Viên</p>
                    <input title='Mã sinh viên' name='maSV' className='form-control' type="text" onChange={this.handleState} onBlur={this.handleValidation} minLength ={2} maxLength={6}/>
                    <span style={{color:'red'}}>{this.state.errorMess.maSV}</span>
                </div>
                <div className='col-6 text-left'>
                    <p className='mt-2'>Họ Tên</p>
                    <input required title='Họ tên'  name='hoTen' className=' form-control' type="text"  onChange={this.handleState} onBlur={this.handleValidation} />
                </div>
                <div className='col-6 text-left'>
                    <p className=' mt-2'>Số Điện Thoại</p>
                    <input required title='Số điện thoại'  name='soDT' className='form-control' type="text" onChange={this.handleState} onBlur={this.handleVailidation} />
                </div>
                <div className='col-6 text-left'>
                    <p className='mt-2'>Email</p>
                    <input pattern='^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$' required title='Email' name='email' className='form-control' type="text" onChange={this.handleState} onBlur={this.handleValidation} />
                </div>
                </div>
                <div className='text-left my-4 '>
                <button className='btn btn-success mr-5 font-weight-bold'> Thêm Sinh Viên</button>
                <button className='btn btn-warning font-weight-bold'> Cập nhật</button>
                </div>



            </form>
        )
    }
}
